                        loc dept_cco.location_id%type)
    is
        idd pls_integer := seqDep.nextval;
    begin
        if ((existsEmp(manager) = true or manager is null) and existsLoc(loc) = true) then
            insert into dept_cco(department_id, department_name, manager_id, location_id)
                values(idd, nume, manager, loc);
        else
            dbms_output.put_line(''Nu exista angajatul sau locatia.'');
        end if;
        if manager is not null then
            update employees set department_id = idd where idd = employee_id;
        end if;
    end createDep;

    procedure addEmp(   fname emp_cco.first_name%type,
                        lname emp_cco.last_name%type,
                        email emp_cco.email%type,
                        hdate emp_cco.hire_date%type,
                        idJob emp_cco.job_id%type,
                        idDep emp_cco.department_id%type)
    is
        idEmp pls_integer := seqEmp.nextval;
    begin
        if existsDep(idDep) = true then
            insert into emp_cco(employee_id, first_name, last_name, email, hire_date, job_id, department_id)
                values(idEmp, fname, lname, email, hdate, idJob, idDep);
        else
            dbms_output.put_line(''Nu exista angajatul.'');
        end if;
    end addEmp;
end ex2;
