    type tp is table of rec;
    dept tp := tp();
    
    type recf is record(nume varchar2(200), vechime number(3), salariu number(10));
    type tpf is table of recf;
    ang tpf := tpf();
    
    loc NUMBER(10) := 0;
    ant NUMBER(10) := 0;
BEGIN
    WITH dayss AS(
    SELECT dd, nr, department_id FROM(
     SELECT TO_CHAR (hire_date , 'day') dd, COUNT(employee_id) nr, department_id
     FROM employees
     GROUP BY TO_CHAR (hire_date , 'day'), department_id) tabl1
     WHERE nr = (SELECT MAX(nr)
                  FROM (    SELECT TO_CHAR (hire_date , 'day') dd, COUNT(employee_id) nr, department_id
                           FROM employees
                           GROUP BY TO_CHAR (hire_date , 'day'), department_id) tabl2
                  WHERE tabl2.department_id = tabl1.department_id)
     )
    SELECT department_id, department_name, dd, nr
    BULK COLLECT INTO dept
    FROM dayss JOIN departments USING(department_id)
    ORDER BY department_name;

  FOR i IN dept.FIRST..dept.LAST LOOP
    DBMS_OUTPUT.PUT_LINE(dept(i).dptid || ' ' || dept(i).dptname || ' ' || dept(i).hired || ' ' || dept(i).cate);
