  BEGIN
    SELECT employee_id
    INTO managerId
    FROM employees
    WHERE last_name = nume
    AND first_name  = prenume;
    RETURN managerId;
  END;
-------------------------------------------------------------------------------
  FUNCTION getSalariu(
      deptId employees.department_id%type,
      jobId employees.job_id%type)
    RETURN employees.salary%type
  IS
    salariuMinim employees.salary%type;
  BEGIN
    SELECT MIN(salary)
    INTO salariuMinim
    FROM employees
    WHERE department_id = deptId
    AND job_id          = jobId;
    RETURN salariuMinim;
  END;
-------------------------------------------------------------------------------
  PROCEDURE addEmployee(
      nume employees.last_name%type,
      prenume employees.first_name%type,
      telefon employees.phone_number%type,
      email employees.email%type,
      numeMan employees.last_name%type,
      prenumeMan employees.first_name%type,
      numeDept departments.department_name%type,
      numeJob jobs.job_title%type)
  IS
    salariuMinim employees.salary%type;
    codManager employees.employee_id%type;
    codDepartament employees.department_id%type;
    codJob employees.job_id%type;
  BEGIN
    codManager     := getManager(numeMan,prenumeMan);
    codDepartament := getCodDepartament(numeDept);
    codJob         := getCodJob(numeJob);
    salariuMinim   := getSalariu(codDepartament,codJob);
    dbms_output.put_line(idEmployeeSecv.nextval || ' ' || codManager || '' || codDepartament || '' || codJob || '' || salariuMinim);
  END;
  PROCEDURE moveEmployee(
      nume employees.last_name%type,
      prenume employees.first_name%type ,
      numeDept departments.department_name%type,
      numeJob employees.job_id%type ,
      numeMan employees.last_name%type,
      prenumeMan employees.first_name%type)
  IS
    idDept departments.department_id%type;
    codJob employees.job_id%type;
    codManager employees.employee_id%type;
    salariuMinim employees.salary%type;
    codDepartament employees.department_id%type;
  BEGIN
    codDepartament := getCodDepartament(numeDept);
    dbms_output.put_line('1');
    codJob := getCodJob(numeJob);
    dbms_output.put_line('2');
    codManager := getManager(numeMan,prenumeMan);
    dbms_output.put_line('3');
    salariuMinim := getSalariu(codDepartament,codJob);
    dbms_output.put_line(salariuMinim);
    SELECT salary
    INTO salariuMinim
    FROM employees
    WHERE first_name = prenumeMan
    AND last_name    = numeMan
    AND salary      >= salariuMinim;
    dbms_output.put_line(salariuMinim);
  END;
-------------------------------------------------------------------------------
  FUNCTION getManagerId(
      empid employees.employee_id%type)
    RETURN employees.employee_id%type
  IS
    manid employees.employee_id%type;
  BEGIN
    SELECT manager_id INTO manid FROM employees WHERE employee_id = empid;
    RETURN manid;
  END;
-------------------------------------------------------------------------------
  FUNCTION getIdJob(
      empid employees.employee_id%type)
    RETURN employees.job_id%type
  IS
    idJob employees.job_id%type;
  BEGIN
    SELECT job_id INTO idJob FROM employees WHERE employee_id = empid;
    RETURN idJob;
  END;
-------------------------------------------------------------------------------
  FUNCTION getIdDept(
      empid employees.employee_id%type)
    RETURN employees.department_id%type
  IS
    idDept employees.department_id%type;
  BEGIN
    SELECT department_id INTO idDept FROM employees WHERE employee_id = empid;
    RETURN idDept;
  END;
-------------------------------------------------------------------------------
  FUNCTION getSalary(
      empid employees.employee_id%type)
    RETURN employees.salary%type
  IS
    sal employees.salary%type;
  BEGIN
    SELECT salary INTO sal FROM employees WHERE employee_id = empid;
    RETURN sal;
  END;
-------------------------------------------------------------------------------
  PROCEDURE promoveaza(
      empid employees.employee_id%type)
  IS
    idManager employees.employee_id%type;
    idJob employees.job_id%type;
    idDept employees.department_id%type;
    newSalary employees.salary%type;
    idManagerManager employees.employee_id%type;
  BEGIN
    idManager        := getManagerId(empid);
    idJob            := getIdJob(idManager);
    idDept           := getIdDept(idManager);
    newSalary        := getSalary(idManager);
    idManagerManager := getManagerId(idManager);
    UPDATE emp_tfo
    SET salary        = newSalary,
      job_id          = idJob,
      department_id   = idDept,
      manager_id      = idManagerManager
    WHERE employee_id = empid;
  END promoveaza;
-------------------------------------------------------------------------------
  FUNCTION subalterni(
      manid employees.employee_id%type)
    RETURN NUMBER
  IS
  type numetip
IS
  TABLE OF employees.employee_id%type INDEX BY binary_integer;
  tabel numetip;
  nrSub NUMBER(4) := 0;
BEGIN
  SELECT employee_id bulk collect
  INTO tabel
  FROM employees
  WHERE manager_id = manid;
  --dbms_output.put_line(nvl(tabel,0));
  IF tabel.count > 0 THEN
    FOR i IN tabel.first..tabel.last
    LOOP
      nrSub := nrSub + subalterni(tabel(i)) + 1;
    END LOOP;
  END IF;
RETURN nrSub;
END subalterni;
-------------------------------------------------------------------------------
PROCEDURE useCursors
IS
BEGIN
  FOR i IN listaJobs
  LOOP
    dbms_output.put_line(i.job_id);
    FOR j IN listaAngajati(i.job_id)
    LOOP
      dbms_output.put_line(j.employee_id);
    END LOOP;
  END LOOP;
END;
-------------------------------------------------------------------------------
PROCEDURE updateSalary(
    ln employees.last_name%type,
    sal employees.salary%type)
IS
  idJob employees.job_id%type;
  minim jobs.min_salary%type;
  maxim jobs.max_salary%type;
Type ntip
IS
  TABLE OF employees.first_name%type INDEX BY binary_integer;
  arrayFN ntip;
BEGIN
  SELECT first_name bulk collect
  INTO arrayFN
  FROM employees
  WHERE last_name  = ln;
  IF arrayFN.count = 1 THEN
    SELECT job_id INTO idJob FROM employees WHERE last_name = ln;
    SELECT min_salary,max_salary INTO minim,maxim FROM jobs WHERE job_id = idJob;
    dbms_output.put_line(minim || ' '|| maxim);
    IF sal >= minim AND sal <= maxim THEN
      dbms_output.put_line('este ok');
    ELSE
      dbms_output.put_line('iese din limite');
    END IF;
  ELSE
    FOR i IN arrayFN.first..arrayFN.last
    LOOP
      dbms_output.put_line(arrayFN(i));
    END LOOP;
  END IF;
END updateSalary;
