--  DDL for Function CHECK_OK
--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "GRUPA34"."CHECK_OK" (
    emp IN employees%ROWTYPE,
    city IN locations.city%TYPE) 
    RETURN NUMBER 
IS 
    job_count NUMBER;
    cur_city locations.city%TYPE;
    cur_loc_id locations.location_id%TYPE;
BEGIN
    -- Compute job count
    SELECT COUNT(*) INTO job_count
    FROM (SELECT DISTINCT job_id
          FROM job_history
          WHERE employee_id = emp.employee_id);

    -- Compute location
    SELECT location_id INTO cur_loc_id
    FROM departments
    WHERE department_id = emp.department_id;

    -- Compute city
    SELECT city INTO cur_city FROM locations
    WHERE location_id = cur_loc_id;

    IF job_count >= 2 AND city = cur_city THEN
        RETURN 1; 
    END IF;

    RETURN 0;
EXCEPTION
    WHEN OTHERS THEN RETURN 0;    
END;

