--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "GRUPA34"."COUNT_EMPS" (
    city IN locations.city%TYPE)
    RETURN NUMBER
IS
    ret NUMBER;
    emp employees%ROWTYPE;

BEGIN
    ret := 0;
    FOR emp IN (SELECT * FROM employees) LOOP
         ret := ret + check_ok(emp, city);
    END LOOP;
    RETURN ret;
END;

