  CREATE OR REPLACE FUNCTION "GRUPA34"."GET_BEST_DAY" 
    (dep_id departments.department_id%TYPE)
    RETURN VARCHAR2
IS
    best_day VARCHAR2(10) := 'N/A';
    best_count NUMBER := 0;
BEGIN
    FOR cur IN (SELECT weekday, COUNT(*) as cnt FROM (
                    SELECT TO_CHAR(hire_date, 'day') AS weekday
                    FROM employees
                    WHERE department_id = dep_id)
                GROUP BY weekday) LOOP
        IF cur.cnt > best_count THEN
            cur.cnt := best_count;
            best_day := cur.weekday;
        END IF;
    END LOOP;
    RETURN best_day;   
END;

