--------------------------------------------------------
--  DDL for Function GET_NR_ANG
--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "GRUPA34"."GET_NR_ANG" (oras LOCATIONS.CITY%type)
RETURN NUMBER
IS
  exceptie EXCEPTION;
  nr_ang NUMBER(2);
  nr_fin_ang NUMBER(2) := 0;
  CURSOR c IS (
    SELECT EMP.EMPLOYEE_ID AS EID, COUNT(*) AS counter 
    FROM EMPLOYEES EMP, JOB_HISTORY JH
    WHERE EMP.EMPLOYEE_ID = JH.EMPLOYEE_ID
    GROUP BY EMP.EMPLOYEE_ID
    HAVING COUNT(*) >= 2
  );
  
BEGIN
  FOR i IN c
  LOOP
      dbms_output.put_line(i.EID);
      SELECT NVL(COUNT(*),0) AS counter
      INTO nr_ang
      FROM EMPLOYEES E, DEPARTMENTS D, LOCATIONS L
      WHERE E.DEPARTMENT_ID = D.DEPARTMENT_ID AND
      D.LOCATION_ID = L.LOCATION_ID AND
      L.CITY = oras AND
      E.EMPLOYEE_ID = i.EID;
      nr_fin_ang := nr_fin_ang + nr_ang;
     
  END LOOP;
    IF nr_fin_ang = 0 THEN
        RAISE exceptie;
    END IF;
  RETURN nr_fin_ang;
EXCEPTION
  WHEN exceptie
  THEN
    --RAISE_APPLICATION_ERROR(-20000, 'nu exista orasul dat');
    dbms_output.put_line('nu exista orasul dat');
  RETURN 0;
END;

